mapper.select:

	select name, email, gender
	  from user
     where no = #{no }


	select no, name
	  from user
	 where email = #{email }
	   and password = #{password }

choose.when:

	update user
	   set name = #{name },
	       gender = #{gender},
	       password = #{password }
	 where no = #{no }

choose.otherwise:

	update user
	   set name = #{name },
	       gender = #{gender }
	 where no = #{no }

mapper.select:

	select no, name
	  from user
	 where email = #{email }
